badjs = {
		'fb' : {
			'title': 'Facebook',
			'image' : '',
			'color' : '#3c568d', 
			'ocolor': '#333333'
		},
		'linkedin' : {
			'title': 'LinkedIn',
			'image' : '',
			'color': '#1b86be',
			'ocolor': '#333333'
		},
		'twitter' : {
			'title': 'Twitter',
			'image' : '',
			'color': '#00acee',
			'ocolor': '#333333'
		},
		'mail' : {
			'title': 'Mail',
			'image' : '',
			'color': '#d54938',
			'ocolor': '#333333'
		},
		'phone' : {
			'title': 'Phone',
			'image' : '',
			'color': '#60c63e',
			'ocolor': '#333333'
		}
	};
	
jQuery.fn.extend({
	badjIt: function() {
		/* then we get data from current badje */
		data = badjs[$(this).data('badj')];

		/* we construct the badje */	
		$(this).css('cursor', 'pointer');
		$(this).css('background-image', 'url("data:image/png;base64,' + data['image'] + '")');
		$(this).css('background-color', data['color']);
		
		/* we catch event on it */
		$(this).on('mouseover', function() {
			$(this).css('background-color', badjs[$(this).data('badj')]['ocolor']);
		}).on('mouseout', function() {
			$(this).css('background-color', badjs[$(this).data('badj')]['color']);
		}).on('click', function() {
			location.href = $(this).data('url');
		});
	}
});

